import { Component, OnInit, ViewChild} from '@angular/core';
import { SpecialityResult } from 'models/speciality-result.model';
import { Speciality } from 'models/speciality.model';
import { SharedService } from 'src/app/shared.service';
import { NgModule } from '@angular/core';
import { ModalDirective } from 'angular-bootstrap-md';
@Component({
  selector: 'app-show-speciality',
  templateUrl: './show-speciality.component.html',
  styleUrls: ['./show-speciality.component.css']
})
export class ShowSpecialityComponent implements OnInit {
  @ViewChild('Modal', {static: true}) Modal: ModalDirective;
  constructor(private service:SharedService) { }
  SpecialityList: SpecialityResult = new SpecialityResult();
  ngOnInit(): void {
    this.RefreshSpecialityList();   
  }

  ModalTitle:string;
  ActivateAddEditSpecialityComp:boolean=false;
  speciality:Speciality= new Speciality();
  async RefreshSpecialityList(){

  await this.service.getSpecialityList().then((data) => {

    if (data.success) {
     this.SpecialityList=data;


    } else {
      alert(data.userMessage);
    }
});
  }
  addClick(){
    this.ModalTitle="Add Speciality";
    this.Modal.show();
    this.ActivateAddEditSpecialityComp=true;
  }
  closeClick(){
    this.ActivateAddEditSpecialityComp=false;
    this.Modal.hide();
    this.RefreshSpecialityList();
  }

  show_add(){
    this.ModalTitle="Apply a new student";
    this.ActivateAddStudentComp=true;
    this.Modal.show();
  }
  close(){
    this.Modal.hide();
     this.refreshStudentList();
        this.ActivateAddStudentComp=false;
        this.ActivateEditStudentComp = false;


  }
  delete_student(student: Students){
    if(confirm('Are you sure?')){
      let result = new StudentsResult();
      this.service
      .DeleteStudent(student.student_id)
      .then((data) => {
        result.success = data.success;
        result.userMessage = data.userMessage;
        if (result.success) {
          alert('You have successfully deleted student');
        } 
        this.refreshStudentList();
        this.student = new Students();
      })
      .catch((error) => {
        alert(
          error.error.userMessage +
            'Student does not exist'
        );
      });
    }
  }
  show_edit(item:Students){
    this.student = item;
    this.ModalTitle="Edit student";
    this.ActivateEditStudentComp = true;
    this.Modal.show();

  }
}





<button type="button" class="btn btn-primary float-right m-2" 
data-toggle="modal" data-target="#exampleModal"
(click)="addClick()"
data-backdrop="static" data-keyboard="false">
    Add Speciality
  </button>
  <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog" role="document">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="exampleModalLabel">{{ModalTitle}}</h5>
          <button type="button" class="close"
          data-dismiss="modal" aria-label="Close"
          (click)="closeClick()">
        <span aria-hidden="true">
            &times;
        </span></button>

        </div>
        <div class="modal-body">
            <app-add-edit-speciality [speciality] = "speciality" *ngIf="ActivateAddEditSpecialityComp">
            </app-add-edit-speciality>
        </div>
      </div>
    </div>
  </div>



<table class="table table-striped">
    <thead>
        <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Options</th>

        </tr>
    </thead>
    <tbody>
         <tr *ngFor="let speciality of SpecialityList.result_set">
            <td>{{speciality.speciality_id}}</td>
            <td>{{speciality.speciality_name}}</td>
            <td>                <button type="button" class="btn btn-light mr-1"
                data-toggle="modal" data-target="#exampleModal"

data-backdrop="static" data-keyboard="false"
                >
                <svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-pencil-square" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                  <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456l-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                  <path fill-rule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                </svg>
                </button>
                <button type="button" class="btn btn-light mr-1">
                  <svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-trash-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5a.5.5 0 0 0-1 0v7a.5.5 0 0 0 1 0v-7z"/>
                  </svg>
                </button>
            </td>

        </tr>
    </tbody>
</table>
