import { Component, OnInit } from '@angular/core';
import { StudentsResult } from 'models/students-result.model';
import { Students } from 'models/students.model';
import { SharedService } from 'src/app/shared.service';

@Component({
  selector: 'app-add-edit-students',
  templateUrl: './add-edit-students.component.html',
  styleUrls: ['./add-edit-students.component.css']
})
export class AddEditStudentsComponent implements OnInit {
  currentStudent: Application = new Application();
  grades: GradesResult = new GradesResult();
  load: string = 'no-show';
  disabled: string = '';

  constructor(private service:SharedService) { }

  ngOnInit(): void {
  }

CurrentStudent: Students;
  async SubmitApplication() {
    let result = new StudentsResult();
    this.disabled = 'disabled';
    this.load = '';
    await this.service
      .AddStudent(this.currentApplication)
      .then((data) => {
        result.success = data.success;
        result.userMessage = data.userMessage;
        result.id = data.result_set.application_ResultSet.id;
        if (result.success) {
          alert('Your reference code is: application' + result.id);
        } else {
          alert(result.userMessage);
        }
        this.currentApplication = new Application();
      })
      .catch((error) => {
        alert(
          error.error.userMessage +
            ' Please make sure you have provided all the values'
        );
      });
    this.disabled = '';
    this.load = 'no-show';
  }
}
