        <button type="button" class = "btn-close" data-mdb-dismiss="modal" aria-label="Close" (click) = "close()" ></button>
       </div>
        <div class="modal-body" >


            <app-add-edit-students [student] = "student" [ActivateAddStudentComp] = "ActivateAddStudentComp"  [ActivateEditStudentComp] = "ActivateEditStudentComp"></app-add-edit-students>

        </div>

      </div>
    </div>
  </div>


<table class="table table-striped">
    <thead>
        <tr>
            <th>Id</th>
