import { Component, OnInit } from '@angular/core';
import { StudentsResult } from 'models/students-result.model';
import { SharedService } from 'src/app/shared.service';
import { FacultyResult } from 'models/faculty-result.model';
import { Faculty } from 'models/faculty.model';
import { stringify } from '@angular/compiler/src/util';

@Component({
  selector: 'app-show-students',
  templateUrl: './show-students.component.html',
  styleUrls: ['./show-students.component.css']
})
export class ShowStudentsComponent implements OnInit {

  constructor(private service:SharedService) { }

  StudentList:StudentsResult= new StudentsResult();
  facultyNames: Faculty = new Faculty();
  ngOnInit(): void {
    this.refreshStudentList();

  }

  public async facultyName(val:number){

    await this.service.getFacultyName(val).then((data) =>{
      this.facultyNames = data;
      let names: string;
      names = this.facultyNames.faculty_name;
      return names;

    });

  }
  async refreshStudentList(){

    this.StudentList.result_set = [];

    await this.service.getStudentList().then((data) => {

      if (data.success) {
       this.StudentList=data;


      } else {
        alert(data.userMessage);
      }
  });
  for(let k=0; k<this.StudentList.result_set.length;k++ ){
  await this.service.getFacultyName(this.StudentList.result_set[k].faculty_id).then((fac_id) =>
  this.StudentList.result_set[k].faculty_name = fac_id.faculty_name
  )

  }
}
}
